sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {},
    isDatePast: function(strDate) {
        // Create GlideDateTime objects for the current date and the passed in date
        var rightnow = new GlideDateTime();
        var whenNeeded = new GlideDateTime(strDate);

		gs.info("Me! rightnow date time: " + rightnow);
		gs.info("Me! whenNeeded date time: " + whenNeeded);

        // If the testdate is before rightnow, return true, else return false
        if (whenNeeded.before(rightnow)) {
            return true;
        } else {
            return false;
        }

    },
    isDateToday: function(strDate) {
        // Create GlideDateTime objects for the current date and the passed in date
        var rightnow = new GlideDateTime().getLocalDate();
        var whenNeeded = new GlideDateTime(strDate).getLocalDate();

		gs.info("Me! rightnow date : " + rightnow);
		gs.info("Me! whenNeeded date: " + whenNeeded);

		if(rightnow.compareTo(whenNeeded) == 0) {
			return true;
		} else {
			return false;
		}
    },
    type: 'NeedItUtils'
};
